  const { language, direction } = useLanguage();
  const [publications, setPublications] = useState<Publication[]>([]);
  const [loading, setLoading] = useState(true);

  const pageTitle = language === 'ar' ? 'البحث العلمي والابتكار' : 'Research & Innovation';
