      ? categoryMap[category?.toLowerCase()]?.ar || category
      : categoryMap[category?.toLowerCase()]?.en || category;
  };

  const stats = [
    { icon: FileText, value: 85, suffix: "+", label: language === 'ar' ? "بحث منشور" : "Publications" },
