                                    <div className="text-right">
                                        <p className="text-sm font-semibold text-white">{page.views.toLocaleString()}</p>
                                        <p className={`text-xs ${page.change.startsWith("+") ? "text-emerald-400" : "text-red-400"}`}>{page.change}</p>
                                    </div>
