      { path: "/institute/management/training", priority: 0.7, changefreq: "weekly" },
      { path: "/institute/management/activities", priority: 0.7, changefreq: "weekly" },

      // Engineering Institute routes
      { path: "/institute/engineering", priority: 0.9, changefreq: "weekly" },
      { path: "/institute/engineering/about", priority: 0.8, changefreq: "weekly" },
      { path: "/institute/engineering/departments", priority: 0.8, changefreq: "weekly" },
      { path: "/institute/engineering/admission", priority: 0.9, changefreq: "daily" },
      { path: "/institute/engineering/labs", priority: 0.7, changefreq: "weekly" },
      { path: "/institute/engineering/news", priority: 0.8, changefreq: "daily" },
      { path: "/institute/engineering/quality", priority: 0.7, changefreq: "weekly" },
      { path: "/institute/engineering/library", priority: 0.7, changefreq: "weekly" },
      { path: "/institute/engineering/contact", priority: 0.7, changefreq: "monthly" },
      { path: "/institute/engineering/student-services", priority: 0.7, changefreq: "weekly" },
      { path: "/institute/engineering/faculty", priority: 0.8, changefreq: "weekly" },
      { path: "/institute/engineering/research", priority: 0.8, changefreq: "weekly" },
      { path: "/institute/engineering/training", priority: 0.7, changefreq: "weekly" },
    ];

    const sitemapXml = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
${sitemapRoutes
  .map(
    (route) => `  <url>
    <loc>${baseUrl}${route.path}</loc>
    <lastmod>${today}</lastmod>
    <changefreq>${route.changefreq}</changefreq>
    <priority>${route.priority}</priority>
  </url>`
  )
  .join("\n")}
</urlset>`;

    res.type("application/xml").send(sitemapXml);
  });

  const httpServer = createServer(app);
  return httpServer;
}

client/src/context/AdminAuthContext.tsx.content:
import { createContext, useContext, useState, type ReactNode } from "react";

export type AdminRole =
    | "super_admin"
    | "admin"
    | "content_editor"
    | "faculty_manager"
    | "student_affairs"
    | "library_manager"
    | "analytics_viewer";

export interface AdminUser {
    id: string;
    name: string;
    nameAr: string;
    email: string;
    role: AdminRole;
    avatar?: string;
}

interface AdminAuthContextType {
    admin: AdminUser | null;
    isAuthenticated: boolean;
    token: string | null;
    login: (email: string, password: string) => Promise<boolean>;
    logout: () => void;
    hasPermission: (permission: Permission) => boolean;
}

// ── Permissions ───────────────────────────────────────────────────────────────
export type Permission =
    | "view_dashboard"
    | "view_analytics"
    | "manage_content"
    | "manage_news"
    | "manage_media"
    | "manage_faculty"
    | "manage_departments"
    | "manage_students"
    | "manage_library"
    | "manage_activities"
    | "manage_users"
    | "manage_settings"
    | "manage_system"
    | "view_logs";

const ROLE_PERMISSIONS: Record<AdminRole, Permission[]> = {
    super_admin: [
        "view_dashboard", "view_analytics", "manage_content", "manage_news",
        "manage_media", "manage_faculty", "manage_departments", "manage_students",
        "manage_library", "manage_activities", "manage_users", "manage_settings",
        "manage_system", "view_logs",
    ],
    admin: [
        "view_dashboard", "view_analytics", "manage_content", "manage_news",
        "manage_media", "manage_faculty", "manage_departments", "manage_students",
        "manage_library", "manage_activities", "view_logs",
    ],
    content_editor: [
        "view_dashboard", "manage_content", "manage_news", "manage_media",
    ],
    faculty_manager: [
        "view_dashboard", "manage_faculty", "manage_departments",
    ],
    student_affairs: [
        "view_dashboard", "manage_students", "manage_activities",
    ],
    library_manager: [
        "view_dashboard", "manage_library",
    ],
    analytics_viewer: [
        "view_dashboard", "view_analytics", "view_logs",
    ],
};

// ── Demo Accounts ─────────────────────────────────────────────────────────────
const DEMO_ACCOUNTS: Array<AdminUser & { password: string }> = [
    {
        id: "1", name: "Super Admin", nameAr: "المدير العام",
        email: "admin@sva.edu.eg", password: "admin123",
        role: "super_admin",
    },
    {
        id: "2", name: "Admin", nameAr: "مدير",
        email: "manager@sva.edu.eg", password: "manager123",
        role: "admin",
    },
    {
        id: "3", name: "Content Editor", nameAr: "محرر المحتوى",
        email: "editor@sva.edu.eg", password: "editor123",
        role: "content_editor",
    },
    {
        id: "4", name: "Faculty Manager", nameAr: "مدير هيئة التدريس",
        email: "faculty@sva.edu.eg", password: "faculty123",
        role: "faculty_manager",
    },
    {
        id: "5", name: "Student Affairs", nameAr: "شؤون الطلاب",
        email: "students@sva.edu.eg", password: "students123",
        role: "student_affairs",
    },
    {
        id: "6", name: "Library Manager", nameAr: "أمين المكتبة",
        email: "library@sva.edu.eg", password: "library123",
        role: "library_manager",
    },
    {
        id: "7", name: "Analytics Viewer", nameAr: "مشاهد الإحصائيات",
        email: "analytics@sva.edu.eg", password: "analytics123",
        role: "analytics_viewer",
    },
];

const AdminAuthContext = createContext<AdminAuthContextType | undefined>(undefined);

export function AdminAuthProvider({ children }: { children: ReactNode }) {
    const [admin, setAdmin] = useState<AdminUser | null>(() => {
        try {
            const stored = localStorage.getItem("admin_user");
            return stored ? JSON.parse(stored) : null;
        } catch {
            return null;
        }
    });

    const [token, setToken] = useState<string | null>(() => {
        try {
            return localStorage.getItem("admin_token");
        } catch {
            return null;
        }
    });

    const isAuthenticated = admin !== null;

    const login = async (email: string, password: string): Promise<boolean> => {
        try {
            // Try API login first
            const response = await fetch("/api/admin/auth/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email, password }),
            });

            if (response.ok) {
                const data = await response.json();
                setAdmin(data.admin);
                setToken(data.token);
                localStorage.setItem("admin_user", JSON.stringify(data.admin));
                localStorage.setItem("admin_token", data.token);
                return true;
            }
        } catch (e) {
            // Fall back to demo login
        }

        // Fallback: Demo login
        await new Promise(r => setTimeout(r, 600));
        const account = DEMO_ACCOUNTS.find(
            a => a.email.toLowerCase() === email.toLowerCase() && a.password === password
        );
        if (account) {
            const { password: _, ...user } = account;
            const mockToken = `mock-token-${user.id}`;
            setAdmin(user);
            setToken(mockToken);
            localStorage.setItem("admin_user", JSON.stringify(user));
            localStorage.setItem("admin_token", mockToken);
            return true;
        }
        return false;
    };

    const logout = () => {
        setAdmin(null);
        setToken(null);
        localStorage.removeItem("admin_user");
        localStorage.removeItem("admin_token");
    };

    const hasPermission = (permission: Permission): boolean => {
        if (!admin) return false;
        return ROLE_PERMISSIONS[admin.role]?.includes(permission) ?? false;
    };

    return (
        <AdminAuthContext.Provider value={{ admin, isAuthenticated, token, login, logout, hasPermission }}>
            {children}
        </AdminAuthContext.Provider>
    );
}

export function useAdminAuth() {
    const ctx = useContext(AdminAuthContext);
    if (!ctx) throw new Error("useAdminAuth must be used within AdminAuthProvider");
    return ctx;
}

