          </AnimatedSection>

          <AnimatedSection delay={0.1}>
            <div className="flex flex-wrap items-center justify-center gap-4 mt-6" dir={direction}>
              {Object.entries(typeLabels).map(([key, val]) => (
                <div key={key} className="flex items-center gap-2">
                  <div className={`w-3 h-3 rounded-full ${typeColors[key].dot}`} />
                  <span className="text-xs font-medium text-neutral-500 dark:text-neutral-400 [font-family:'Almarai',Helvetica]">
                    {language === 'ar' ? val.ar : val.en}
                  </span>
                </div>
              ))}
            </div>
          </AnimatedSection>
        </div>
      </section>

      <section className="py-12 md:py-16 bg-gray-50 dark:bg-neutral-900 transition-colors duration-300">
        <div className="max-w-[900px] mx-auto px-4 md:px-8">
          <div className="relative" dir={direction}>
            <div className={`absolute top-0 bottom-0 ${direction === 'rtl' ? 'right-6' : 'left-6'} w-0.5 bg-green-200 dark:bg-green-900/40`} />

            {currentEvents.map((event, index) => {
              const colors = typeColors[event.type];
              const Icon = event.icon;
              return (
                <AnimatedSection key={index} delay={index * 0.05} direction={direction === 'rtl' ? 'right' : 'left'}>
                  <div className={`relative flex items-start gap-5 mb-6 ${direction === 'rtl' ? 'pr-0' : 'pl-0'}`}>
                    <div className={`relative z-10 w-12 h-12 rounded-full ${colors.bg} flex items-center justify-center shrink-0 border-4 border-white dark:border-neutral-900`}>
                      <Icon className={`w-5 h-5 ${colors.text}`} />
                    </div>
                    <Card className="flex-1 rounded-2xl border-0 shadow-sm hover:shadow-md transition-all bg-white dark:bg-neutral-800">
                      <CardContent className="p-5">
                        <div className="flex items-start justify-between gap-3 flex-wrap">
                          <div>
                            <h3 className="font-bold text-neutral-900 dark:text-white [font-family:'Almarai',Helvetica] text-base mb-1">
                              {language === 'ar' ? event.titleAr : event.titleEn}
                            </h3>
                            <span className={`inline-block text-xs font-bold px-2.5 py-1 rounded-full ${colors.bg} ${colors.text} [font-family:'Almarai',Helvetica]`}>
                              {language === 'ar' ? typeLabels[event.type].ar : typeLabels[event.type].en}
                            </span>
                          </div>
                          <div className="flex items-center gap-2 shrink-0">
                            <Clock className="w-4 h-4 text-neutral-400" />
                            <span className="text-sm font-medium text-neutral-500 dark:text-neutral-400 [font-family:'Almarai',Helvetica]">
                              {language === 'ar' ? event.date : event.dateEn}
                            </span>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  </div>
                </AnimatedSection>
              );
            })}
          </div>
        </div>
      </section>

      {apiEvents.length > 0 && (
        <section className="py-12 md:py-16 bg-white dark:bg-neutral-950 transition-colors duration-300">
          <div className="max-w-[900px] mx-auto px-4 md:px-8">
            <AnimatedSection>
              <div className="text-center mb-10" dir={direction}>
                <h3 className="text-2xl md:text-3xl font-bold text-neutral-900 dark:text-white [font-family:'Almarai',Helvetica] transition-colors duration-300">
                  {language === 'ar' ? 'فعاليات أكاديمية إضافية' : 'Additional Academic Events'}
                </h3>
              </div>
            </AnimatedSection>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4" dir={direction}>
              {apiEvents.map((event, index) => (
                <AnimatedSection key={event.id || index} delay={index * 0.05} direction="up">
                  <Card className="rounded-2xl border-0 shadow-sm hover:shadow-md transition-all bg-white dark:bg-neutral-800">
                    <CardContent className="p-5 flex items-center gap-4">
                      <div className="w-12 h-12 rounded-full bg-green-50 dark:bg-green-900/20 flex items-center justify-center shrink-0">
                        <Calendar className="w-5 h-5 text-green-700 dark:text-green-500" />
                      </div>
                      <div className="flex-1 min-w-0">
                        <h4 className="font-bold text-neutral-900 dark:text-white [font-family:'Almarai',Helvetica] text-sm mb-1">
                          {language === 'ar' ? (event.titleAr || event.title) : (event.titleEn || event.titleAr || event.title)}
                        </h4>
                        {event.date && (
                          <span className="text-xs text-neutral-500 dark:text-neutral-400 [font-family:'Almarai',Helvetica]">
                            {new Date(event.date).toLocaleDateString(language === 'ar' ? 'ar-EG' : 'en-US', { year: 'numeric', month: 'long', day: 'numeric' })}
                          </span>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                </AnimatedSection>
              ))}
            </div>
          </div>
        </section>
      )}

      <section className="py-16 md:py-20 bg-white dark:bg-neutral-950 transition-colors duration-300">
        <div className="max-w-[900px] mx-auto px-4 md:px-8">
          <AnimatedSection>
            <Card className="rounded-3xl border-0 shadow-md bg-amber-50 dark:bg-amber-900/10 overflow-hidden">
              <CardContent className="p-8 md:p-10" dir={direction}>
                <div className="flex items-center gap-3 mb-6">
                  <div className="w-10 h-10 rounded-xl bg-amber-100 dark:bg-amber-900/30 flex items-center justify-center">
                    <AlertCircle className="w-5 h-5 text-amber-600 dark:text-amber-400" />
                  </div>
                  <h3 className="text-xl font-bold text-amber-800 dark:text-amber-300 [font-family:'Almarai',Helvetica]">
                    {language === 'ar' ? 'ملاحظات هامة' : 'Important Notes'}
                  </h3>
                </div>
                <ul className="space-y-3">
                  {importantNotes.map((note, i) => (
                    <li key={i} className="flex items-start gap-3">
                      <div className="w-2 h-2 rounded-full bg-amber-500 mt-2 shrink-0" />
                      <span className="text-amber-800 dark:text-amber-200 [font-family:'Almarai',Helvetica] text-sm leading-relaxed">
                        {language === 'ar' ? note.ar : note.en}
                      </span>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          </AnimatedSection>
        </div>
      </section>

      <Footer />
    </div>
  );
}

client/src/pages/admin/StudentAffairsMan.content:
import { useState, useEffect } from "react";
import { AdminLayout } from "./AdminLayout";
import { useAdminAuth } from "@/context/AdminAuthContext";
import { motion } from "framer-motion";
import {
  GraduationCap, FileText, Award, HelpCircle, Megaphone, Save,
  Loader2, CheckCircle2, Users, DollarSign, BookOpen, Plus, Trash2, ToggleLeft, ToggleRight
} from "lucide-react";
import { Link } from "wouter";

interface AdmissionInfo {
  requirementsAr: string[];
  requirementsEn: string[];
  deadlineAr: string;
  deadlineEn: string;
  contactEmail: string;
  contactPhone: string;
}

interface Scholarship {
  nameAr: string;
  nameEn: string;
  valueAr: string;
  valueEn: string;
  criteriaAr: string;
  criteriaEn: string;
  isActive: boolean;
}

type Tab = "admissions" | "scholarships" | "faqs" | "announcements";

const defaultAdmission: AdmissionInfo = {
  requirementsAr: [
    "شهادة الثانوية العامة المصرية أو ما يعادلها",
    "الحد الأدنى 50% في المسار العلمي/الأدبي",
    "بطاقة رقم قومي أو جواز سفر ساري",
    "4 صور شخصية حديثة",
    "شهادة الموقف من التجنيد (للذكور)",
    "شهادة اللياقة الطبية"
  ],
  requirementsEn: [
    "Egyptian General Secondary Certificate or equivalent",
    "Minimum 50% in scientific/literary track",
    "Valid national ID or passport",
    "4 recent passport-size photographs",
    "Military status certificate (for males)",
    "Medical fitness certificate"
  ],
  deadlineAr: "من 1 يوليو إلى 30 سبتمبر",
  deadlineEn: "July 1 to September 30",
  contactEmail: "admissions@valleyinstitutes.edu.eg",
  contactPhone: "+20 123 456 789",
};

const defaultScholarships: Scholarship[] = [
  { nameAr: "منحة التفوق الأكاديمي", nameEn: "Academic Excellence Award", valueAr: "تخفيض 50%", valueEn: "50% reduction", criteriaAr: "أفضل 5% من دفعة التخرج بمعدل 90%+", criteriaEn: "Top 5% with 90%+ grade", isActive: true },
  { nameAr: "منحة الاحتياج المالي", nameEn: "Financial Need Grant", valueAr: "تخفيض 25-75%", valueEn: "25-75% reduction", criteriaAr: "بناءً على تقييم دخل الأسرة", criteriaEn: "Based on family income assessment", isActive: true },
  { nameAr: "منحة التفوق الرياضي", nameEn: "Sports Excellence Grant", valueAr: "تخفيض 30%", valueEn: "30% reduction", criteriaAr: "إنجاز رياضي وطني أو دولي", criteriaEn: "National/international sports achievement", isActive: true },
  { nameAr: "خصم الأشقاء", nameEn: "Sibling Discount", valueAr: "10% لكل شقيق", valueEn: "10% per sibling", criteriaAr: "طالبان أو أكثر مسجلين", criteriaEn: "2+ enrolled students", isActive: true },
];

export default function StudentAffairsManager() {
  const { token } = useAdminAuth();
  const [activeTab, setActiveTab] = useState<Tab>("admissions");
  const [saving, setSaving] = useState(false);
  const [saved, setSaved] = useState(false);
  const [loading, setLoading] = useState(true);
  const [admission, setAdmission] = useState<AdmissionInfo>(defaultAdmission);
  const [scholarships, setScholarships] = useState<Scholarship[]>(defaultScholarships);

  useEffect(() => {
    if (!token) return;
    fetch("/api/admin/settings", { headers: { Authorization: `Bearer ${token}` } })
      .then(r => r.json())
      .then((data) => {
        if (!Array.isArray(data)) return;
        const find = (key: string) => data.find((s: any) => s.key === key)?.value;
        if (find("student_admission")) setAdmission(find("student_admission"));
        if (find("student_scholarships")) setScholarships(find("student_scholarships"));
      })
      .catch(() => {})
      .finally(() => setLoading(false));
  }, [token]);

  const saveSettings = async (key: string, value: any) => {
    setSaving(true);
    try {
      await fetch(`/api/admin/settings/${key}`, {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
        body: JSON.stringify({ value, group: "students" }),
      });
      setSaved(true);
      setTimeout(() => setSaved(false), 2000);
    } catch {
    } finally {
      setSaving(false);
    }
  };

  const tabs = [
    { id: "admissions" as Tab, label: "Admissions", icon: FileText },
    { id: "scholarships" as Tab, label: "Scholarships", icon: Award },
    { id: "faqs" as Tab, label: "Student FAQs", icon: HelpCircle },
    { id: "announcements" as Tab, label: "Announcements", icon: Megaphone },
  ];

  const inputClass = "w-full bg-slate-800 border border-slate-700 rounded-xl px-4 py-3 text-white placeholder-slate-500 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 outline-none transition-colors";
  const labelClass = "block text-sm font-medium text-slate-300 mb-1.5";

  return (
    <AdminLayout>
      <div className="p-6 max-w-5xl mx-auto">
        <motion.div initial={{ opacity: 0, y: 12 }} animate={{ opacity: 1, y: 0 }} className="mb-8">
          <div className="flex items-center justify-between flex-wrap gap-4">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-2xl bg-emerald-500/10 border border-emerald-500/20 flex items-center justify-center">
                <GraduationCap className="w-6 h-6 text-emerald-400" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-white">Student Affairs</h1>
                <p className="text-slate-400 text-sm">Manage admissions, scholarships, and student services</p>
              </div>
            </div>
            {saved && (
              <span className="flex items-center gap-1.5 text-emerald-400 text-sm">
                <CheckCircle2 className="w-4 h-4" /> Saved successfully
              </span>
            )}
          </div>
        </motion.div>

        <div className="flex gap-2 mb-8 flex-wrap">
          {tabs.map(tab => (
            <button key={tab.id} onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-4 py-2.5 rounded-xl text-sm font-medium transition-all ${
                activeTab === tab.id
                  ? "bg-emerald-600 text-white shadow-lg shadow-emerald-500/20"
                  : "bg-slate-800 text-slate-400 hover:bg-slate-700 hover:text-white"
              }`}>
              <tab.icon className="w-4 h-4" />
              {tab.label}
            </button>
          ))}
        </div>

        {loading ? (
          <div className="flex justify-center py-20">
            <Loader2 className="w-8 h-8 text-emerald-400 animate-spin" />
          </div>
        ) : (
          <motion.div key={activeTab} initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}
            className="bg-slate-900 border border-slate-800 rounded-2xl p-6">

            {activeTab === "admissions" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Admission Information</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className={labelClass}>Application Deadline (Arabic)</label>
                    <input className={inputClass} dir="rtl" value={admission.deadlineAr}
                      onChange={e => setAdmission({ ...admission, deadlineAr: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Application Deadline (English)</label>
                    <input className={inputClass} value={admission.deadlineEn}
                      onChange={e => setAdmission({ ...admission, deadlineEn: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Contact Email</label>
                    <input className={inputClass} type="email" value={admission.contactEmail}
                      onChange={e => setAdmission({ ...admission, contactEmail: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Contact Phone</label>
                    <input className={inputClass} value={admission.contactPhone}
                      onChange={e => setAdmission({ ...admission, contactPhone: e.target.value })} />
                  </div>
                </div>

                <div>
                  <label className={labelClass}>Requirements (Arabic) - one per line</label>
                  <textarea className={inputClass} dir="rtl" rows={6}
                    value={admission.requirementsAr.join("\n")}
                    onChange={e => setAdmission({ ...admission, requirementsAr: e.target.value.split("\n").filter(Boolean) })} />
                </div>
                <div>
                  <label className={labelClass}>Requirements (English) - one per line</label>
                  <textarea className={inputClass} rows={6}
                    value={admission.requirementsEn.join("\n")}
                    onChange={e => setAdmission({ ...admission, requirementsEn: e.target.value.split("\n").filter(Boolean) })} />
                </div>

                <div className="flex justify-end pt-4">
                  <button onClick={() => saveSettings("student_admission", admission)} disabled={saving}
                    className="flex items-center gap-2 px-6 py-2.5 bg-emerald-600 hover:bg-emerald-500 text-white rounded-xl font-medium transition-colors disabled:opacity-50">
                    {saving ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
                    Save Admission Info
                  </button>
                </div>
              </div>
            )}

            {activeTab === "scholarships" && (
              <div className="space-y-6">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-white">Scholarships & Financial Aid</h2>
                  <button onClick={() => setScholarships([...scholarships, { nameAr: "", nameEn: "", valueAr: "", valueEn: "", criteriaAr: "", criteriaEn: "", isActive: true }])}
                    className="flex items-center gap-1.5 px-3 py-1.5 bg-emerald-600 hover:bg-emerald-500 text-white rounded-lg text-sm transition-colors">
                    <Plus className="w-3.5 h-3.5" /> Add Scholarship
                  </button>
                </div>
                {scholarships.map((s, i) => (
                  <div key={i} className="bg-slate-800 border border-slate-700 rounded-xl p-5">
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center gap-3">
                        <span className="text-sm font-medium text-slate-300">Scholarship #{i + 1}</span>
                        <button onClick={() => {
                          const sc = [...scholarships]; sc[i] = { ...sc[i], isActive: !sc[i].isActive }; setScholarships(sc);
                        }}
                          className={`px-2 py-0.5 rounded-full text-xs font-medium ${s.isActive ? 'bg-emerald-500/10 text-emerald-400' : 'bg-red-500/10 text-red-400'}`}>
                          {s.isActive ? "Active" : "Inactive"}
                        </button>
                      </div>
                      <button onClick={() => setScholarships(scholarships.filter((_, idx) => idx !== i))}
                        className="text-red-400 hover:text-red-300 p-1">
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <label className={labelClass}>Name (Arabic)</label>
                        <input className={inputClass} dir="rtl" value={s.nameAr}
                          onChange={e => { const sc = [...scholarships]; sc[i] = { ...sc[i], nameAr: e.target.value }; setScholarships(sc); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Name (English)</label>
                        <input className={inputClass} value={s.nameEn}
                          onChange={e => { const sc = [...scholarships]; sc[i] = { ...sc[i], nameEn: e.target.value }; setScholarships(sc); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Value (Arabic)</label>
                        <input className={inputClass} dir="rtl" value={s.valueAr}
                          onChange={e => { const sc = [...scholarships]; sc[i] = { ...sc[i], valueAr: e.target.value }; setScholarships(sc); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Value (English)</label>
                        <input className={inputClass} value={s.valueEn}
                          onChange={e => { const sc = [...scholarships]; sc[i] = { ...sc[i], valueEn: e.target.value }; setScholarships(sc); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Criteria (Arabic)</label>
                        <input className={inputClass} dir="rtl" value={s.criteriaAr}
                          onChange={e => { const sc = [...scholarships]; sc[i] = { ...sc[i], criteriaAr: e.target.value }; setScholarships(sc); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Criteria (English)</label>
                        <input className={inputClass} value={s.criteriaEn}
                          onChange={e => { const sc = [...scholarships]; sc[i] = { ...sc[i], criteriaEn: e.target.value }; setScholarships(sc); }} />
                      </div>
                    </div>
                  </div>
                ))}
                <div className="flex justify-end pt-4">
                  <button onClick={() => saveSettings("student_scholarships", scholarships)} disabled={saving}
                    className="flex items-center gap-2 px-6 py-2.5 bg-emerald-600 hover:bg-emerald-500 text-white rounded-xl font-medium transition-colors disabled:opacity-50">
                    {saving ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
                    Save Scholarships
                  </button>
                </div>
              </div>
            )}

            {activeTab === "faqs" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Student FAQs</h2>
                <p className="text-slate-400 text-sm mb-6">
                  Manage frequently asked questions through the FAQs Manager.
                  FAQs categorized as "Admission", "Financial", or "Student Life" appear on the Students page.
                </p>
                <Link href="/admin/students/faqs">
                  <div className="bg-slate-800 border border-slate-700 hover:border-emerald-500/50 rounded-xl p-6 transition-colors cursor-pointer">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 rounded-xl bg-violet-500/10 flex items-center justify-center">
                        <HelpCircle className="w-6 h-6 text-violet-400" />
                      </div>
                      <div>
                        <h3 className="text-white font-medium text-lg">FAQs Manager</h3>
                        <p className="text-slate-500 text-sm">Create, edit, and organize frequently asked questions</p>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            )}

            {activeTab === "announcements" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Student Announcements</h2>
                <p className="text-slate-400 text-sm mb-6">
                  Manage student-related announcements through the Announcements Manager.
                </p>
                <Link href="/admin/announcements">
                  <div className="bg-slate-800 border border-slate-700 hover:border-amber-500/50 rounded-xl p-6 transition-colors cursor-pointer">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 rounded-xl bg-amber-500/10 flex items-center justify-center">
                        <Megaphone className="w-6 h-6 text-amber-400" />
                      </div>
                      <div>
                        <h3 className="text-white font-medium text-lg">Announcements Manager</h3>
                        <p className="text-slate-500 text-sm">Create and manage student announcements and notifications</p>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            )}
          </motion.div>
        )}
      </div>
    </AdminLayout>
  );
}

client/src/pages/admin/AcademicManager.t.content:
import { useState, useEffect } from "react";
import { AdminLayout } from "./AdminLayout";
import { useAdminAuth } from "@/context/AdminAuthContext";
import { motion } from "framer-motion";
import {
  GraduationCap, Calendar, BookOpen, Clock, Save, Loader2,
  CheckCircle2, Plus, Trash2, BookMarked, FileText, Building2
} from "lucide-react";
import { Link } from "wouter";

interface SemesterSchedule {
  nameAr: string;
  nameEn: string;
  startDate: string;
  endDate: string;
  events: { labelAr: string; labelEn: string; date: string }[];
}

interface AcademicCalendarSettings {
  currentYear: string;
  semesters: SemesterSchedule[];
}

type Tab = "calendar" | "schedules" | "courses" | "departments";

const defaultCalendar: AcademicCalendarSettings = {
  currentYear: "2025/2026",
  semesters: [
    {
      nameAr: "الفصل الدراسي الأول",
      nameEn: "First Semester",
      startDate: "2025-10-01",
      endDate: "2026-01-31",
      events: [
        { labelAr: "بداية الدراسة", labelEn: "Classes Begin", date: "2025-10-01" },
        { labelAr: "امتحانات نصف الفصل", labelEn: "Midterm Exams", date: "2025-11-24" },
        { labelAr: "إجازة نصف العام", labelEn: "Mid-Year Break", date: "2026-01-18" },
        { labelAr: "الامتحانات النهائية", labelEn: "Final Exams", date: "2026-01-25" },
      ],
    },
    {
      nameAr: "الفصل الدراسي الثاني",
      nameEn: "Second Semester",
      startDate: "2026-02-08",
      endDate: "2026-06-15",
      events: [
        { labelAr: "بداية الدراسة", labelEn: "Classes Begin", date: "2026-02-08" },
        { labelAr: "امتحانات نصف الفصل", labelEn: "Midterm Exams", date: "2026-04-06" },
        { labelAr: "الامتحانات النهائية", labelEn: "Final Exams", date: "2026-06-01" },
        { labelAr: "نهاية العام الدراسي", labelEn: "Academic Year End", date: "2026-06-15" },
      ],
    },
  ],
};

export default function AcademicManager() {
  const { token } = useAdminAuth();
  const [activeTab, setActiveTab] = useState<Tab>("calendar");
  const [saving, setSaving] = useState(false);
  const [saved, setSaved] = useState(false);
  const [loading, setLoading] = useState(true);
  const [calendar, setCalendar] = useState<AcademicCalendarSettings>(defaultCalendar);
  const [courseCount, setCourseCount] = useState(0);
  const [deptCount, setDeptCount] = useState(0);

  useEffect(() => {
    if (!token) return;
    Promise.all([
      fetch("/api/admin/settings", { headers: { Authorization: `Bearer ${token}` } }).then(r => r.json()).catch(() => []),
      fetch("/api/courses").then(r => r.json()).catch(() => []),
      fetch("/api/departments").then(r => r.json()).catch(() => []),
    ])
      .then(([settings, courses, depts]) => {
        if (Array.isArray(settings)) {
          const find = (key: string) => settings.find((s: any) => s.key === key)?.value;
          if (find("academic_calendar")) setCalendar(find("academic_calendar"));
        }
        if (Array.isArray(courses)) setCourseCount(courses.length);
        if (Array.isArray(depts)) setDeptCount(depts.length);
      })
      .catch(() => {})
      .finally(() => setLoading(false));
  }, [token]);

  const saveSettings = async (key: string, value: any) => {
    setSaving(true);
    try {
      await fetch(`/api/admin/settings/${key}`, {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
        body: JSON.stringify({ value, group: "academic" }),
      });
      setSaved(true);
      setTimeout(() => setSaved(false), 2000);
    } catch {
    } finally {
      setSaving(false);
    }
  };

  const tabs = [
    { id: "calendar" as Tab, label: "Academic Calendar", icon: Calendar },
    { id: "schedules" as Tab, label: "Exam Schedules", icon: Clock },
    { id: "courses" as Tab, label: "Course Catalog", icon: BookMarked },
    { id: "departments" as Tab, label: "Departments", icon: Building2 },
  ];

  const inputClass = "w-full bg-slate-800 border border-slate-700 rounded-xl px-4 py-3 text-white placeholder-slate-500 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 outline-none transition-colors";
  const labelClass = "block text-sm font-medium text-slate-300 mb-1.5";

  const updateSemester = (semIdx: number, field: string, value: any) => {
    const updated = { ...calendar };
    (updated.semesters[semIdx] as any)[field] = value;
    setCalendar({ ...updated });
  };

  const updateSemesterEvent = (semIdx: number, evtIdx: number, field: string, value: string) => {
    const updated = { ...calendar };
    (updated.semesters[semIdx].events[evtIdx] as any)[field] = value;
    setCalendar({ ...updated });
  };

  const addEventToSemester = (semIdx: number) => {
    const updated = { ...calendar };
    updated.semesters[semIdx].events.push({ labelAr: "", labelEn: "", date: "" });
    setCalendar({ ...updated });
  };

  const removeEventFromSemester = (semIdx: number, evtIdx: number) => {
    const updated = { ...calendar };
    updated.semesters[semIdx].events = updated.semesters[semIdx].events.filter((_, i) => i !== evtIdx);
    setCalendar({ ...updated });
  };

  return (
    <AdminLayout>
      <div className="p-6 max-w-5xl mx-auto">
        <motion.div initial={{ opacity: 0, y: 12 }} animate={{ opacity: 1, y: 0 }} className="mb-8">
          <div className="flex items-center justify-between flex-wrap gap-4">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-2xl bg-violet-500/10 border border-violet-500/20 flex items-center justify-center">
                <GraduationCap className="w-6 h-6 text-violet-400" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-white">Academic System</h1>
                <p className="text-slate-400 text-sm">Manage academic calendar, schedules, and course catalog</p>
              </div>
            </div>
            {saved && (
              <span className="flex items-center gap-1.5 text-emerald-400 text-sm">
                <CheckCircle2 className="w-4 h-4" /> Saved successfully
              </span>
            )}
          </div>
        </motion.div>

        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
          <div className="bg-slate-900 border border-slate-800 rounded-xl p-4">
            <p className="text-2xl font-bold text-white">{calendar.currentYear}</p>
            <p className="text-slate-500 text-xs">Current Year</p>
          </div>
          <div className="bg-slate-900 border border-slate-800 rounded-xl p-4">
            <p className="text-2xl font-bold text-violet-400">{calendar.semesters.length}</p>
            <p className="text-slate-500 text-xs">Semesters</p>
          </div>
          <div className="bg-slate-900 border border-slate-800 rounded-xl p-4">
            <p className="text-2xl font-bold text-emerald-400">{courseCount}</p>
            <p className="text-slate-500 text-xs">Courses</p>
          </div>
          <div className="bg-slate-900 border border-slate-800 rounded-xl p-4">
            <p className="text-2xl font-bold text-amber-400">{deptCount}</p>
            <p className="text-slate-500 text-xs">Departments</p>
          </div>
        </div>

        <div className="flex gap-2 mb-8 flex-wrap">
          {tabs.map(tab => (
            <button key={tab.id} onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-4 py-2.5 rounded-xl text-sm font-medium transition-all ${
                activeTab === tab.id
                  ? "bg-violet-600 text-white shadow-lg shadow-violet-500/20"
                  : "bg-slate-800 text-slate-400 hover:bg-slate-700 hover:text-white"
              }`}>
              <tab.icon className="w-4 h-4" />
              {tab.label}
            </button>
          ))}
        </div>

        {loading ? (
          <div className="flex justify-center py-20">
            <Loader2 className="w-8 h-8 text-violet-400 animate-spin" />
          </div>
        ) : (
          <motion.div key={activeTab} initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}
            className="bg-slate-900 border border-slate-800 rounded-2xl p-6">

            {activeTab === "calendar" && (
              <div className="space-y-6">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-white">Academic Calendar</h2>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                  <div>
                    <label className={labelClass}>Academic Year</label>
                    <input className={inputClass} value={calendar.currentYear}
                      onChange={e => setCalendar({ ...calendar, currentYear: e.target.value })}
                      placeholder="e.g. 2025/2026" />
                  </div>
                </div>

                {calendar.semesters.map((sem, si) => (
                  <div key={si} className="bg-slate-800 border border-slate-700 rounded-xl p-5 mb-4">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-white font-medium">{sem.nameEn || `Semester ${si + 1}`}</h3>
                      <button onClick={() => addEventToSemester(si)}
                        className="flex items-center gap-1 px-2 py-1 bg-violet-600 hover:bg-violet-500 text-white rounded-lg text-xs transition-colors">
                        <Plus className="w-3 h-3" /> Add Event
                      </button>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                      <div>
                        <label className={labelClass}>Name (Arabic)</label>
                        <input className={inputClass} dir="rtl" value={sem.nameAr}
                          onChange={e => updateSemester(si, "nameAr", e.target.value)} />
                      </div>
                      <div>
                        <label className={labelClass}>Name (English)</label>
                        <input className={inputClass} value={sem.nameEn}
                          onChange={e => updateSemester(si, "nameEn", e.target.value)} />
                      </div>
                      <div>
                        <label className={labelClass}>Start Date</label>
                        <input className={inputClass} type="date" value={sem.startDate}
                          onChange={e => updateSemester(si, "startDate", e.target.value)} />
                      </div>
                      <div>
                        <label className={labelClass}>End Date</label>
                        <input className={inputClass} type="date" value={sem.endDate}
                          onChange={e => updateSemester(si, "endDate", e.target.value)} />
                      </div>
                    </div>

                    <p className="text-xs font-semibold text-slate-500 uppercase tracking-widest mb-3">Key Dates</p>
                    {sem.events.map((evt, ei) => (
                      <div key={ei} className="grid grid-cols-5 gap-3 mb-2 items-center">
                        <input className={`${inputClass} text-sm col-span-1`} dir="rtl" placeholder="Arabic" value={evt.labelAr}
                          onChange={e => updateSemesterEvent(si, ei, "labelAr", e.target.value)} />
                        <input className={`${inputClass} text-sm col-span-1`} placeholder="English" value={evt.labelEn}
                          onChange={e => updateSemesterEvent(si, ei, "labelEn", e.target.value)} />
                        <input className={`${inputClass} text-sm col-span-2`} type="date" value={evt.date}
                          onChange={e => updateSemesterEvent(si, ei, "date", e.target.value)} />
                        <button onClick={() => removeEventFromSemester(si, ei)}
                          className="text-red-400 hover:text-red-300 p-2 justify-self-center">
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    ))}
                  </div>
                ))}

                <div className="flex justify-between pt-4">
                  <button onClick={() => {
                    const updated = { ...calendar };
                    updated.semesters.push({ nameAr: "", nameEn: "", startDate: "", endDate: "", events: [] });
                    setCalendar(updated);
                  }}
                    className="flex items-center gap-1.5 px-4 py-2 bg-slate-800 hover:bg-slate-700 text-slate-300 rounded-xl text-sm transition-colors">
                    <Plus className="w-4 h-4" /> Add Semester
                  </button>
                  <button onClick={() => saveSettings("academic_calendar", calendar)} disabled={saving}
                    className="flex items-center gap-2 px-6 py-2.5 bg-violet-600 hover:bg-violet-500 text-white rounded-xl font-medium transition-colors disabled:opacity-50">
                    {saving ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
                    Save Calendar
                  </button>
                </div>
              </div>
            )}

            {activeTab === "schedules" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Exam Schedules</h2>
                <p className="text-slate-400 text-sm mb-6">
                  Exam schedules are managed through the Events Manager. Create events with the "academic" category
                  to have them appear in the Academic Calendar.
                </p>
                <Link href="/admin/events">
                  <div className="bg-slate-800 border border-slate-700 hover:border-violet-500/50 rounded-xl p-6 transition-colors cursor-pointer">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 rounded-xl bg-amber-500/10 flex items-center justify-center">
                        <Clock className="w-6 h-6 text-amber-400" />
                      </div>
                      <div>
                        <h3 className="text-white font-medium text-lg">Events Manager</h3>
                        <p className="text-slate-500 text-sm">Create exam schedules as academic events</p>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            )}

            {activeTab === "courses" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Course Catalog</h2>
                <p className="text-slate-400 text-sm mb-6">
                  Manage the full course catalog across all departments and institutes.
                  Currently {courseCount} courses registered.
                </p>
                <Link href="/admin/courses">
                  <div className="bg-slate-800 border border-slate-700 hover:border-violet-500/50 rounded-xl p-6 transition-colors cursor-pointer">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 rounded-xl bg-emerald-500/10 flex items-center justify-center">
                        <BookMarked className="w-6 h-6 text-emerald-400" />
                      </div>
                      <div>
                        <h3 className="text-white font-medium text-lg">Courses Manager</h3>
                        <p className="text-slate-500 text-sm">Create, edit, and organize courses with bilingual support</p>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            )}

            {activeTab === "departments" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Academic Departments</h2>
                <p className="text-slate-400 text-sm mb-6">
                  Manage academic departments across Engineering and Management institutes.
                  Currently {deptCount} departments registered.
                </p>
                <Link href="/admin/departments">
                  <div className="bg-slate-800 border border-slate-700 hover:border-violet-500/50 rounded-xl p-6 transition-colors cursor-pointer">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 rounded-xl bg-indigo-500/10 flex items-center justify-center">
                        <Building2 className="w-6 h-6 text-indigo-400" />
                      </div>
                      <div>
                        <h3 className="text-white font-medium text-lg">Departments Manager</h3>
                        <p className="text-slate-500 text-sm">Manage department structure and details</p>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            )}
          </motion.div>
        )}
      </div>
    </AdminLayout>
  );
}

