import { useState, useEffect } from "react";
import { AdminLayout } from "./AdminLayout";
import { useAdminAuth } from "@/context/AdminAuthContext";
import { motion } from "framer-motion";
import {
    Plus, Search, Edit2, Trash2, Activity, Users,
    Trophy, X, Check, Upload, Clock,
    FileText, Building2, AlertTriangle, Eye, EyeOff,
    Palette, Heart, FlaskConical
} from "lucide-react";

const categories = ["All", "Club", "Sport", "Cultural", "Volunteer", "Scientific"];

interface ActivityItem {
    id: string;
    nameAr: string;
    nameEn: string;
    descriptionAr: string;
    descriptionEn: string;
    category: string;
    coverImage: string;
    institute: string;
    isActive: boolean;
}

const emptyActivity: Omit<ActivityItem, "id"> & { id?: string } = {
    nameAr: "", nameEn: "", descriptionAr: "", descriptionEn: "",
    category: "Club", coverImage: "",
    institute: "both", isActive: true
};

const categoryIcons: Record<string, React.ElementType> = {
    Club: Users,
    Sport: Trophy,
    Cultural: Palette,
    Volunteer: Heart,
    Scientific: FlaskConical,
};

const categoryColors: Record<string, string> = {
    Club: "text-indigo-400 bg-indigo-500/10",
    Sport: "text-emerald-400 bg-emerald-500/10",
    Cultural: "text-amber-400 bg-amber-500/10",
    Volunteer: "text-rose-400 bg-rose-500/10",
    Scientific: "text-cyan-400 bg-cyan-500/10",
};

function ActivityModal({ activity, onClose, onSave }: {
    activity: ActivityItem | null;
    onClose: () => void;
    onSave: (e: Omit<ActivityItem, "id"> & { id?: string }) => void;
}) {
    const [form, setForm] = useState<Omit<ActivityItem, "id"> & { id?: string }>(activity || { ...emptyActivity });

    return (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-start justify-center p-4 overflow-y-auto">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-[800px] my-8"
            >
                <div className="flex items-center justify-between p-6 border-b border-slate-800">
                    <h2 className="text-lg font-semibold text-white">{activity ? "Edit Activity" : "New Activity"}</h2>
                    <button onClick={onClose} className="p-2 rounded-xl hover:bg-slate-800 transition-colors text-slate-400">
                        <X className="w-5 h-5" />
                    </button>
                </div>

                <div className="p-6 flex flex-col gap-5">
                    <div className="relative rounded-xl overflow-hidden bg-slate-800 border-2 border-dashed border-slate-600 h-[180px] flex items-center justify-center cursor-pointer hover:border-indigo-500 transition-colors group">
                        {form.coverImage ? (
                            <img src={form.coverImage} alt="" className="w-full h-full object-cover" />
                        ) : (
                            <div className="flex flex-col items-center gap-2 text-slate-500 group-hover:text-slate-300 transition-colors">
                                <Upload className="w-8 h-8" />
                                <span className="text-sm">Upload cover image</span>
                            </div>
                        )}
                    </div>

                    <div className="flex flex-col gap-1.5">
                        <label className="text-sm font-medium text-slate-300">Cover Image URL</label>
                        <input value={form.coverImage} onChange={e => setForm({ ...form, coverImage: e.target.value })}
                            placeholder="https://..."
                            className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Name (English)</label>
                            <input value={form.nameEn} onChange={e => setForm({ ...form, nameEn: e.target.value })}
                                placeholder="Activity name..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">الاسم (عربي)</label>
                            <input value={form.nameAr} onChange={e => setForm({ ...form, nameAr: e.target.value })} dir="rtl"
                                placeholder="اسم النشاط..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all text-right" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Description (English)</label>
                            <textarea value={form.descriptionEn} onChange={e => setForm({ ...form, descriptionEn: e.target.value })}
                                rows={4} placeholder="Activity description..."
                                className="px-4 py-3 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all resize-none" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">الوصف (عربي)</label>
                            <textarea value={form.descriptionAr} onChange={e => setForm({ ...form, descriptionAr: e.target.value })}
                                rows={4} dir="rtl" placeholder="وصف النشاط..."
                                className="px-4 py-3 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all resize-none text-right" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Category</label>
                            <select value={form.category} onChange={e => setForm({ ...form, category: e.target.value })}
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all">
                                {categories.filter(c => c !== "All").map(c => <option key={c}>{c}</option>)}
                            </select>
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Institute</label>
                            <select value={form.institute} onChange={e => setForm({ ...form, institute: e.target.value })}
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all">
                                <option value="engineering">Engineering</option>
                                <option value="management">Management</option>
                                <option value="both">Both</option>
                            </select>
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Status</label>
                            <button
                                onClick={() => setForm({ ...form, isActive: !form.isActive })}
                                className={`flex items-center gap-2 px-4 py-2.5 rounded-xl border text-sm transition-all ${form.isActive
                                        ? "bg-emerald-500/10 border-emerald-500/30 text-emerald-400"
                                        : "bg-slate-800 border-slate-700 text-slate-400"
                                    }`}
                            >
                                {form.isActive ? <Eye className="w-4 h-4" /> : <EyeOff className="w-4 h-4" />}
                                {form.isActive ? "Active" : "Inactive"}
                            </button>
                        </div>
                    </div>
                </div>

                <div className="flex items-center justify-end gap-3 p-6 border-t border-slate-800">
                    <button onClick={onClose} className="px-5 py-2.5 bg-slate-800 hover:bg-slate-700 text-slate-300 text-sm rounded-xl transition-colors">
                        Cancel
                    </button>
                    <button onClick={() => onSave(form)} className="px-5 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white text-sm font-semibold rounded-xl transition-colors flex items-center gap-2">
                        <Check className="w-4 h-4" />
                        {activity ? "Save Changes" : "Create Activity"}
                    </button>
                </div>
            </motion.div>
        </div>
    );
}

function DeleteConfirmModal({ onClose, onConfirm, title }: {
    onClose: () => void;
    onConfirm: () => void;
    title: string;
}) {
    return (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4">
            <motion.div
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                className="bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-[420px] p-6"
            >
                <div className="flex items-center gap-3 mb-4">
                    <div className="w-10 h-10 rounded-full bg-red-500/10 flex items-center justify-center">
                        <AlertTriangle className="w-5 h-5 text-red-400" />
                    </div>
                    <div>
                        <h3 className="text-white font-semibold">Delete Activity</h3>
                        <p className="text-slate-400 text-sm">This action cannot be undone.</p>
                    </div>
                </div>
                <p className="text-slate-300 text-sm mb-6">
                    Are you sure you want to delete "<span className="text-white font-medium">{title}</span>"?
                </p>
                <div className="flex items-center justify-end gap-3">
                    <button onClick={onClose} className="px-5 py-2.5 bg-slate-800 hover:bg-slate-700 text-slate-300 text-sm rounded-xl transition-colors">
                        Cancel
                    </button>
                    <button onClick={onConfirm} className="px-5 py-2.5 bg-red-600 hover:bg-red-500 text-white text-sm font-semibold rounded-xl transition-colors">
                        Delete
                    </button>
                </div>
            </motion.div>
        </div>
    );
}

export default function ActivitiesManager() {
    const { token } = useAdminAuth();
    const [activities, setActivities] = useState<ActivityItem[]>([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState("");
    const [catFilter, setCatFilter] = useState("All");
    const [modalOpen, setModalOpen] = useState(false);
    const [editActivity, setEditActivity] = useState<ActivityItem | null>(null);
    const [deleteTarget, setDeleteTarget] = useState<ActivityItem | null>(null);

    const headers = {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
    };

    const fetchActivities = async () => {
        try {
            const res = await fetch("/api/admin/activities", { headers });
            if (res.ok) {
                const data = await res.json();
                setActivities(data);
            }
        } catch {
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => { fetchActivities(); }, []);

    const filtered = activities.filter(a => {
        const matchCat = catFilter === "All" || a.category === catFilter;
        const matchSearch = !search || (a.nameEn || "").toLowerCase().includes(search.toLowerCase()) || (a.nameAr || "").includes(search);
        return matchCat && matchSearch;
    });

    const handleSave = async (form: Omit<ActivityItem, "id"> & { id?: string }) => {
        try {
            if (editActivity) {
                const res = await fetch(`/api/admin/activities/${editActivity.id}`, {
                    method: "PUT", headers, body: JSON.stringify(form),
                });
                if (res.ok) {
                    const updated = await res.json();
                    setActivities(prev => prev.map(a => a.id === editActivity.id ? updated : a));
                }
            } else {
                const res = await fetch("/api/admin/activities", {
                    method: "POST", headers, body: JSON.stringify(form),
                });
                if (res.ok) {
                    const created = await res.json();
                    setActivities(prev => [...prev, created]);
                }
            }
        } catch {
        }
        setModalOpen(false);
        setEditActivity(null);
    };

    const handleDelete = async () => {
        if (!deleteTarget) return;
        try {
            const res = await fetch(`/api/admin/activities/${deleteTarget.id}`, {
                method: "DELETE", headers,
            });
            if (res.ok) {
                setActivities(prev => prev.filter(a => a.id !== deleteTarget.id));
            }
        } catch {
        }
        setDeleteTarget(null);
    };

    const toggleActive = async (activity: ActivityItem) => {
        try {
            const res = await fetch(`/api/admin/activities/${activity.id}`, {
                method: "PUT", headers,
                body: JSON.stringify({ ...activity, isActive: !activity.isActive }),
            });
            if (res.ok) {
                const updated = await res.json();
                setActivities(prev => prev.map(a => a.id === activity.id ? updated : a));
            }
        } catch {
        }
    };

    return (
        <AdminLayout>
            {(modalOpen || editActivity) && (
                <ActivityModal
                    activity={editActivity}
                    onClose={() => { setModalOpen(false); setEditActivity(null); }}
                    onSave={handleSave}
                />
            )}

            {deleteTarget && (
                <DeleteConfirmModal
                    title={deleteTarget.nameEn || deleteTarget.nameAr}
                    onClose={() => setDeleteTarget(null)}
                    onConfirm={handleDelete}
                />
            )}

            <div className="p-6 md:p-8 max-w-[1400px] mx-auto">
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-8">
                    <div>
                        <h1 className="text-2xl font-bold text-white">Activities Manager</h1>
                        <p className="text-slate-400 text-sm mt-1">{activities.length} total activities</p>
                    </div>
                    <button
                        onClick={() => { setEditActivity(null); setModalOpen(true); }}
                        className="flex items-center gap-2 px-5 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white text-sm font-semibold rounded-xl transition-colors shadow-lg shadow-indigo-500/20"
                    >
                        <Plus className="w-4 h-4" />
                        New Activity
                    </button>
                </div>

                <div className="grid grid-cols-2 md:grid-cols-4 xl:grid-cols-7 gap-4 mb-6">
                    {[
                        { label: "Active", count: activities.filter(a => a.isActive).length, color: "text-emerald-400" },
                        { label: "Inactive", count: activities.filter(a => !a.isActive).length, color: "text-red-400" },
                        { label: "Club", count: activities.filter(a => a.category === "Club").length, color: "text-indigo-400" },
                        { label: "Sport", count: activities.filter(a => a.category === "Sport").length, color: "text-emerald-400" },
                        { label: "Cultural", count: activities.filter(a => a.category === "Cultural").length, color: "text-amber-400" },
                        { label: "Volunteer", count: activities.filter(a => a.category === "Volunteer").length, color: "text-rose-400" },
                        { label: "Scientific", count: activities.filter(a => a.category === "Scientific").length, color: "text-cyan-400" },
                    ].map((s, i) => (
                        <div key={i} className="bg-slate-900 border border-slate-800 rounded-xl p-4 text-center">
                            <p className={`text-2xl font-bold ${s.color}`}>{s.count}</p>
                            <p className="text-xs text-slate-500 mt-0.5">{s.label}</p>
                        </div>
                    ))}
                </div>

                <div className="flex flex-col md:flex-row gap-4 mb-6">
                    <div className="relative flex-1 max-w-sm">
                        <Search className="absolute left-3.5 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                        <input value={search} onChange={e => setSearch(e.target.value)} placeholder="Search activities..."
                            className="w-full pl-10 pr-4 py-2.5 bg-slate-900 border border-slate-800 rounded-xl text-white text-sm placeholder-slate-500 focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                    </div>
                    <div className="flex flex-wrap gap-2">
                        {categories.map(c => (
                            <button key={c} onClick={() => setCatFilter(c)}
                                className={`px-4 py-2 rounded-xl text-sm transition-all ${catFilter === c ? "bg-indigo-600 text-white" : "bg-slate-900 border border-slate-800 text-slate-400 hover:text-white hover:border-slate-600"}`}>
                                {c}
                            </button>
                        ))}
                    </div>
                </div>

                {loading ? (
                    <div className="flex flex-col items-center justify-center py-20 text-slate-500">
                        <Clock className="w-12 h-12 mb-3 opacity-30 animate-spin" />
                        <p className="text-sm">Loading activities...</p>
                    </div>
                ) : (
                    <>
                        <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-5">
                            {filtered.map((activity, i) => {
                                const CatIcon = categoryIcons[activity.category] || Activity;
                                const catColor = categoryColors[activity.category] || "text-slate-400 bg-slate-500/10";
                                return (
                                    <motion.div
                                        key={activity.id}
                                        initial={{ opacity: 0, y: 16 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ delay: i * 0.06 }}
                                        className="bg-slate-900 border border-slate-800 rounded-2xl overflow-hidden group"
                                    >
                                        <div className="relative h-[160px] overflow-hidden">
                                            {activity.coverImage ? (
                                                <img src={activity.coverImage} alt={activity.nameEn || activity.nameAr} className="w-full h-full object-cover" />
                                            ) : (
                                                <div className="w-full h-full bg-slate-800 flex items-center justify-center">
                                                    <Activity className="w-10 h-10 text-slate-700" />
                                                </div>
                                            )}
                                            <div className="absolute inset-0 bg-gradient-to-t from-slate-900/80 to-transparent" />
                                            <div className="absolute top-3 left-3 flex gap-2">
                                                <span className={`text-xs px-2.5 py-1 rounded-full font-medium ${activity.isActive ? "bg-emerald-500/10 text-emerald-400" : "bg-red-500/10 text-red-400"}`}>
                                                    {activity.isActive ? "Active" : "Inactive"}
                                                </span>
                                            </div>
                                        </div>
                                        <div className="p-4">
                                            <div className="flex items-center gap-2 mb-2">
                                                <span className={`text-xs px-2 py-0.5 rounded-full flex items-center gap-1 ${catColor}`}>
                                                    <CatIcon className="w-3 h-3" />{activity.category}
                                                </span>
                                                {activity.institute && (
                                                    <span className="text-xs text-slate-500 flex items-center gap-1">
                                                        <Building2 className="w-3 h-3" />{activity.institute}
                                                    </span>
                                                )}
                                            </div>
                                            <h3 className="text-sm font-semibold text-white line-clamp-2 mb-1">{activity.nameEn || activity.nameAr}</h3>
                                            {(activity.descriptionEn || activity.descriptionAr) && (
                                                <p className="text-xs text-slate-500 line-clamp-2">
                                                    {activity.descriptionEn || activity.descriptionAr}
                                                </p>
                                            )}
                                        </div>
                                        <div className="flex items-center gap-2 px-4 pb-4">
                                            <button onClick={() => setEditActivity(activity)} className="flex-1 flex items-center justify-center gap-1.5 py-2 bg-slate-800 hover:bg-slate-700 text-slate-300 text-xs rounded-xl transition-colors">
                                                <Edit2 className="w-3.5 h-3.5" /> Edit
                                            </button>
                                            <button onClick={() => toggleActive(activity)} className={`p-2 rounded-xl transition-colors ${activity.isActive ? "bg-emerald-500/10 text-emerald-400" : "bg-slate-800 text-slate-400 hover:text-emerald-400"}`}>
                                                {activity.isActive ? <Eye className="w-3.5 h-3.5" /> : <EyeOff className="w-3.5 h-3.5" />}
                                            </button>
                                            <button onClick={() => setDeleteTarget(activity)} className="p-2 rounded-xl bg-slate-800 hover:bg-red-500/10 text-slate-400 hover:text-red-400 transition-colors">
                                                <Trash2 className="w-3.5 h-3.5" />
                                            </button>
                                        </div>
                                    </motion.div>
                                );
                            })}
                        </div>

                        {filtered.length === 0 && (
                            <div className="flex flex-col items-center justify-center py-20 text-slate-500">
                                <FileText className="w-12 h-12 mb-3 opacity-30" />
                                <p className="text-sm">No activities found</p>
                            </div>
                        )}
                    </>
                )}
            </div>
        </AdminLayout>
    );
}

client/src/pages/admin/HomepageManager.t.content:
import { useState, useEffect } from "react";
import { AdminLayout } from "./AdminLayout";
import { useAdminAuth } from "@/context/AdminAuthContext";
import { motion } from "framer-motion";
import {
  Home, Type, BarChart3, Megaphone, Star, Save, Loader2,
  CheckCircle2, AlertCircle, Image, Eye, RefreshCw, Plus, Trash2
} from "lucide-react";
import { Link } from "wouter";

interface HeroSettings {
  titleAr: string;
  titleEn: string;
  subtitleAr: string;
  subtitleEn: string;
  ctaTextAr: string;
  ctaTextEn: string;
  ctaLink: string;
  backgroundImage: string;
}

interface StatItem {
  labelAr: string;
  labelEn: string;
  value: string;
  suffix: string;
  icon: string;
}

interface AnnouncementTicker {
  textAr: string;
  textEn: string;
  link: string;
  isActive: boolean;
}

const defaultHero: HeroSettings = {
  titleAr: "معاهد الوادي العليا",
  titleEn: "Valley Higher Institutes",
  subtitleAr: "نحو تعليم متميز يبني الأجيال ويخدم المجتمع",
  subtitleEn: "Towards distinguished education that builds generations and serves society",
  ctaTextAr: "تقدم الآن",
  ctaTextEn: "Apply Now",
  ctaLink: "/admission",
  backgroundImage: "/figmaAssets/rectangle-2.png",
};

const defaultStats: StatItem[] = [
  { labelAr: "طالب مسجل", labelEn: "Enrolled Students", value: "3000", suffix: "+", icon: "GraduationCap" },
  { labelAr: "عضو هيئة تدريس", labelEn: "Faculty Members", value: "150", suffix: "+", icon: "Users" },
  { labelAr: "برنامج أكاديمي", labelEn: "Academic Programs", value: "8", suffix: "", icon: "BookOpen" },
  { labelAr: "سنوات خبرة", labelEn: "Years of Experience", value: "20", suffix: "+", icon: "Award" },
];

const defaultAnnouncements: AnnouncementTicker[] = [
  { textAr: "بدأ التسجيل للعام الدراسي الجديد", textEn: "Registration for the new academic year has started", link: "/admission", isActive: true },
];

type Tab = "hero" | "stats" | "announcements" | "featured";

export default function HomepageManager() {
  const { token } = useAdminAuth();
  const [activeTab, setActiveTab] = useState<Tab>("hero");
  const [saving, setSaving] = useState(false);
  const [saved, setSaved] = useState(false);
  const [hero, setHero] = useState<HeroSettings>(defaultHero);
  const [stats, setStats] = useState<StatItem[]>(defaultStats);
  const [announcements, setAnnouncements] = useState<AnnouncementTicker[]>(defaultAnnouncements);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!token) return;
    fetch("/api/admin/settings", { headers: { Authorization: `Bearer ${token}` } })
      .then(r => r.json())
      .then((data) => {
        if (!Array.isArray(data)) return;
        const find = (key: string) => data.find((s: any) => s.key === key)?.value;
        if (find("homepage_hero")) setHero(find("homepage_hero"));
        if (find("homepage_stats")) setStats(find("homepage_stats"));
        if (find("homepage_announcements")) setAnnouncements(find("homepage_announcements"));
      })
      .catch(() => {})
      .finally(() => setLoading(false));
  }, [token]);

  const saveSettings = async (key: string, value: any) => {
    setSaving(true);
    try {
      await fetch(`/api/admin/settings/${key}`, {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
        body: JSON.stringify({ value, group: "homepage" }),
      });
      setSaved(true);
      setTimeout(() => setSaved(false), 2000);
    } catch {
    } finally {
      setSaving(false);
    }
  };

  const tabs = [
    { id: "hero" as Tab, label: "Hero Section", icon: Image },
    { id: "stats" as Tab, label: "Statistics", icon: BarChart3 },
    { id: "announcements" as Tab, label: "Announcements", icon: Megaphone },
    { id: "featured" as Tab, label: "Featured Content", icon: Star },
  ];

  const inputClass = "w-full bg-slate-800 border border-slate-700 rounded-xl px-4 py-3 text-white placeholder-slate-500 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 outline-none transition-colors";
  const labelClass = "block text-sm font-medium text-slate-300 mb-1.5";

  return (
    <AdminLayout>
      <div className="p-6 max-w-5xl mx-auto">
        <motion.div initial={{ opacity: 0, y: 12 }} animate={{ opacity: 1, y: 0 }} className="mb-8">
          <div className="flex items-center justify-between flex-wrap gap-4">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-2xl bg-indigo-500/10 border border-indigo-500/20 flex items-center justify-center">
                <Home className="w-6 h-6 text-indigo-400" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-white">Homepage Control</h1>
                <p className="text-slate-400 text-sm">Customize the homepage sections and content</p>
              </div>
            </div>
            {saved && (
              <span className="flex items-center gap-1.5 text-emerald-400 text-sm">
                <CheckCircle2 className="w-4 h-4" /> Saved successfully
              </span>
            )}
          </div>
        </motion.div>

        <div className="flex gap-2 mb-8 flex-wrap">
          {tabs.map(tab => (
            <button key={tab.id} onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-4 py-2.5 rounded-xl text-sm font-medium transition-all ${
                activeTab === tab.id
                  ? "bg-indigo-600 text-white shadow-lg shadow-indigo-500/20"
                  : "bg-slate-800 text-slate-400 hover:bg-slate-700 hover:text-white"
              }`}>
              <tab.icon className="w-4 h-4" />
              {tab.label}
            </button>
          ))}
        </div>

        {loading ? (
          <div className="flex justify-center py-20">
            <Loader2 className="w-8 h-8 text-indigo-400 animate-spin" />
          </div>
        ) : (
          <motion.div key={activeTab} initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}
            className="bg-slate-900 border border-slate-800 rounded-2xl p-6">

            {activeTab === "hero" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Hero Section Settings</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className={labelClass}>Title (Arabic)</label>
                    <input className={inputClass} dir="rtl" value={hero.titleAr}
                      onChange={e => setHero({ ...hero, titleAr: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Title (English)</label>
                    <input className={inputClass} value={hero.titleEn}
                      onChange={e => setHero({ ...hero, titleEn: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Subtitle (Arabic)</label>
                    <textarea className={inputClass} dir="rtl" rows={3} value={hero.subtitleAr}
                      onChange={e => setHero({ ...hero, subtitleAr: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Subtitle (English)</label>
                    <textarea className={inputClass} rows={3} value={hero.subtitleEn}
                      onChange={e => setHero({ ...hero, subtitleEn: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>CTA Text (Arabic)</label>
                    <input className={inputClass} dir="rtl" value={hero.ctaTextAr}
                      onChange={e => setHero({ ...hero, ctaTextAr: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>CTA Text (English)</label>
                    <input className={inputClass} value={hero.ctaTextEn}
                      onChange={e => setHero({ ...hero, ctaTextEn: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>CTA Link</label>
                    <input className={inputClass} value={hero.ctaLink}
                      onChange={e => setHero({ ...hero, ctaLink: e.target.value })} />
                  </div>
                  <div>
                    <label className={labelClass}>Background Image URL</label>
                    <input className={inputClass} value={hero.backgroundImage}
                      onChange={e => setHero({ ...hero, backgroundImage: e.target.value })} />
                  </div>
                </div>
                <div className="flex justify-end pt-4">
                  <button onClick={() => saveSettings("homepage_hero", hero)} disabled={saving}
                    className="flex items-center gap-2 px-6 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white rounded-xl font-medium transition-colors disabled:opacity-50">
                    {saving ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
                    Save Hero Settings
                  </button>
                </div>
              </div>
            )}

            {activeTab === "stats" && (
              <div className="space-y-6">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-white">Statistics Counters</h2>
                  <button onClick={() => setStats([...stats, { labelAr: "", labelEn: "", value: "0", suffix: "", icon: "Users" }])}
                    className="flex items-center gap-1.5 px-3 py-1.5 bg-emerald-600 hover:bg-emerald-500 text-white rounded-lg text-sm transition-colors">
                    <Plus className="w-3.5 h-3.5" /> Add Stat
                  </button>
                </div>
                {stats.map((stat, i) => (
                  <div key={i} className="bg-slate-800 border border-slate-700 rounded-xl p-5">
                    <div className="flex items-center justify-between mb-4">
                      <span className="text-sm font-medium text-slate-300">Stat #{i + 1}</span>
                      <button onClick={() => setStats(stats.filter((_, idx) => idx !== i))}
                        className="text-red-400 hover:text-red-300 p-1">
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                    <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                      <div>
                        <label className={labelClass}>Label (Arabic)</label>
                        <input className={inputClass} dir="rtl" value={stat.labelAr}
                          onChange={e => { const s = [...stats]; s[i] = { ...s[i], labelAr: e.target.value }; setStats(s); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Label (English)</label>
                        <input className={inputClass} value={stat.labelEn}
                          onChange={e => { const s = [...stats]; s[i] = { ...s[i], labelEn: e.target.value }; setStats(s); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Value</label>
                        <input className={inputClass} value={stat.value}
                          onChange={e => { const s = [...stats]; s[i] = { ...s[i], value: e.target.value }; setStats(s); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Suffix</label>
                        <input className={inputClass} placeholder="e.g. + or %" value={stat.suffix}
                          onChange={e => { const s = [...stats]; s[i] = { ...s[i], suffix: e.target.value }; setStats(s); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Icon Name</label>
                        <select className={inputClass} value={stat.icon}
                          onChange={e => { const s = [...stats]; s[i] = { ...s[i], icon: e.target.value }; setStats(s); }}>
                          <option value="GraduationCap">Graduation Cap</option>
                          <option value="Users">Users</option>
                          <option value="BookOpen">Book</option>
                          <option value="Award">Award</option>
                          <option value="Building2">Building</option>
                          <option value="TrendingUp">Trending Up</option>
                          <option value="Globe">Globe</option>
                        </select>
                      </div>
                    </div>
                  </div>
                ))}
                <div className="flex justify-end pt-4">
                  <button onClick={() => saveSettings("homepage_stats", stats)} disabled={saving}
                    className="flex items-center gap-2 px-6 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white rounded-xl font-medium transition-colors disabled:opacity-50">
                    {saving ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
                    Save Statistics
                  </button>
                </div>
              </div>
            )}

            {activeTab === "announcements" && (
              <div className="space-y-6">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-white">Homepage Announcements</h2>
                  <button onClick={() => setAnnouncements([...announcements, { textAr: "", textEn: "", link: "", isActive: true }])}
                    className="flex items-center gap-1.5 px-3 py-1.5 bg-emerald-600 hover:bg-emerald-500 text-white rounded-lg text-sm transition-colors">
                    <Plus className="w-3.5 h-3.5" /> Add
                  </button>
                </div>
                {announcements.map((ann, i) => (
                  <div key={i} className="bg-slate-800 border border-slate-700 rounded-xl p-5">
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center gap-3">
                        <span className="text-sm font-medium text-slate-300">Announcement #{i + 1}</span>
                        <button onClick={() => {
                          const a = [...announcements]; a[i] = { ...a[i], isActive: !a[i].isActive }; setAnnouncements(a);
                        }}
                          className={`px-2 py-0.5 rounded-full text-xs font-medium ${ann.isActive ? 'bg-emerald-500/10 text-emerald-400' : 'bg-red-500/10 text-red-400'}`}>
                          {ann.isActive ? "Active" : "Inactive"}
                        </button>
                      </div>
                      <button onClick={() => setAnnouncements(announcements.filter((_, idx) => idx !== i))}
                        className="text-red-400 hover:text-red-300 p-1">
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <label className={labelClass}>Text (Arabic)</label>
                        <input className={inputClass} dir="rtl" value={ann.textAr}
                          onChange={e => { const a = [...announcements]; a[i] = { ...a[i], textAr: e.target.value }; setAnnouncements(a); }} />
                      </div>
                      <div>
                        <label className={labelClass}>Text (English)</label>
                        <input className={inputClass} value={ann.textEn}
                          onChange={e => { const a = [...announcements]; a[i] = { ...a[i], textEn: e.target.value }; setAnnouncements(a); }} />
                      </div>
                      <div className="md:col-span-2">
                        <label className={labelClass}>Link (optional)</label>
                        <input className={inputClass} placeholder="/admission" value={ann.link}
                          onChange={e => { const a = [...announcements]; a[i] = { ...a[i], link: e.target.value }; setAnnouncements(a); }} />
                      </div>
                    </div>
                  </div>
                ))}
                <div className="flex justify-end pt-4">
                  <button onClick={() => saveSettings("homepage_announcements", announcements)} disabled={saving}
                    className="flex items-center gap-2 px-6 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white rounded-xl font-medium transition-colors disabled:opacity-50">
                    {saving ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
                    Save Announcements
                  </button>
                </div>
              </div>
            )}

            {activeTab === "featured" && (
              <div className="space-y-6">
                <h2 className="text-lg font-semibold text-white mb-4">Featured Content</h2>
                <p className="text-slate-400 text-sm mb-6">
                  Featured content is automatically pulled from published news, events, and announcements.
                  Use the managers below to control what appears on the homepage.
                </p>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <Link href="/admin/news">
                    <div className="bg-slate-800 border border-slate-700 hover:border-indigo-500/50 rounded-xl p-5 transition-colors cursor-pointer">
                      <div className="w-10 h-10 rounded-xl bg-indigo-500/10 flex items-center justify-center mb-3">
                        <Type className="w-5 h-5 text-indigo-400" />
                      </div>
                      <h3 className="text-white font-medium mb-1">News Manager</h3>
                      <p className="text-slate-500 text-xs">Manage featured news articles</p>
                    </div>
                  </Link>
                  <Link href="/admin/events">
                    <div className="bg-slate-800 border border-slate-700 hover:border-emerald-500/50 rounded-xl p-5 transition-colors cursor-pointer">
                      <div className="w-10 h-10 rounded-xl bg-emerald-500/10 flex items-center justify-center mb-3">
                        <Megaphone className="w-5 h-5 text-emerald-400" />
                      </div>
                      <h3 className="text-white font-medium mb-1">Events Manager</h3>
                      <p className="text-slate-500 text-xs">Manage featured events</p>
                    </div>
                  </Link>
                  <Link href="/admin/announcements">
                    <div className="bg-slate-800 border border-slate-700 hover:border-amber-500/50 rounded-xl p-5 transition-colors cursor-pointer">
                      <div className="w-10 h-10 rounded-xl bg-amber-500/10 flex items-center justify-center mb-3">
                        <AlertCircle className="w-5 h-5 text-amber-400" />
                      </div>
                      <h3 className="text-white font-medium mb-1">Announcements</h3>
                      <p className="text-slate-500 text-xs">Manage site announcements</p>
                    </div>
                  </Link>
                </div>
              </div>
            )}
          </motion.div>
        )}
      </div>
    </AdminLayout>
  );
}

client/src/pages/admin/ResearchManager.t.content:
import { useState, useEffect } from "react";
import { AdminLayout } from "./AdminLayout";
import { useAdminAuth } from "@/context/AdminAuthContext";
import { motion } from "framer-motion";
import {
    Plus, Search, Edit2, Trash2, FlaskConical, BookOpen,
    X, Check, Clock, FileText, Link as LinkIcon,
    Building2, AlertTriangle, ExternalLink, GraduationCap
} from "lucide-react";

const categories = ["All", "Journal Article", "Conference Paper", "Book Chapter", "Thesis", "Technical Report"];

interface ResearchItem {
    id: string;
    titleAr: string;
    titleEn: string;
    abstractAr: string;
    abstractEn: string;
    authorIds: string[];
    publishedYear: number | null;
    journal: string;
    doi: string;
    fileUrl: string;
    externalUrl: string;
    category: string;
    institute: string;
    status: string;
}

const emptyResearch: Omit<ResearchItem, "id"> & { id?: string } = {
    titleAr: "", titleEn: "", abstractAr: "", abstractEn: "",
    authorIds: [], publishedYear: null, journal: "", doi: "",
    fileUrl: "", externalUrl: "", category: "Journal Article",
    institute: "both", status: "draft"
};

const statusColor: Record<string, string> = {
    published: "bg-emerald-500/10 text-emerald-400",
    draft: "bg-slate-700 text-slate-400",
    scheduled: "bg-amber-500/10 text-amber-400",
    archived: "bg-red-500/10 text-red-400",
};

function ResearchModal({ research, onClose, onSave }: {
    research: ResearchItem | null;
    onClose: () => void;
    onSave: (r: Omit<ResearchItem, "id"> & { id?: string }) => void;
}) {
    const [form, setForm] = useState<Omit<ResearchItem, "id"> & { id?: string }>(research || { ...emptyResearch });
    const [authorIdsText, setAuthorIdsText] = useState(
        (research?.authorIds || []).join(", ")
    );

    return (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-start justify-center p-4 overflow-y-auto">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-[800px] my-8"
            >
                <div className="flex items-center justify-between p-6 border-b border-slate-800">
                    <h2 className="text-lg font-semibold text-white">{research ? "Edit Research" : "New Research"}</h2>
                    <button onClick={onClose} className="p-2 rounded-xl hover:bg-slate-800 transition-colors text-slate-400">
                        <X className="w-5 h-5" />
                    </button>
                </div>

                <div className="p-6 flex flex-col gap-5">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Title (English)</label>
                            <input value={form.titleEn} onChange={e => setForm({ ...form, titleEn: e.target.value })}
                                placeholder="Research title..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">العنوان (عربي)</label>
                            <input value={form.titleAr} onChange={e => setForm({ ...form, titleAr: e.target.value })} dir="rtl"
                                placeholder="عنوان البحث..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all text-right" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Abstract (English)</label>
                            <textarea value={form.abstractEn} onChange={e => setForm({ ...form, abstractEn: e.target.value })}
                                rows={4} placeholder="Research abstract..."
                                className="px-4 py-3 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all resize-none" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">الملخص (عربي)</label>
                            <textarea value={form.abstractAr} onChange={e => setForm({ ...form, abstractAr: e.target.value })}
                                rows={4} dir="rtl" placeholder="ملخص البحث..."
                                className="px-4 py-3 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all resize-none text-right" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Author IDs (comma-separated)</label>
                            <input value={authorIdsText} onChange={e => {
                                setAuthorIdsText(e.target.value);
                                setForm({ ...form, authorIds: e.target.value.split(",").map(s => s.trim()).filter(Boolean) });
                            }}
                                placeholder="author1, author2, ..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Published Year</label>
                            <input type="number" value={form.publishedYear ?? ""} onChange={e => setForm({ ...form, publishedYear: e.target.value ? parseInt(e.target.value) : null })}
                                placeholder="2024"
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all [color-scheme:dark]" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Journal</label>
                            <input value={form.journal} onChange={e => setForm({ ...form, journal: e.target.value })}
                                placeholder="Journal name..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">DOI</label>
                            <input value={form.doi} onChange={e => setForm({ ...form, doi: e.target.value })}
                                placeholder="10.1000/xyz123"
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">File URL</label>
                            <input value={form.fileUrl} onChange={e => setForm({ ...form, fileUrl: e.target.value })}
                                placeholder="https://..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">External URL</label>
                            <input value={form.externalUrl} onChange={e => setForm({ ...form, externalUrl: e.target.value })}
                                placeholder="https://..."
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Category</label>
                            <select value={form.category} onChange={e => setForm({ ...form, category: e.target.value })}
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all">
                                {categories.filter(c => c !== "All").map(c => <option key={c}>{c}</option>)}
                            </select>
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Institute</label>
                            <select value={form.institute} onChange={e => setForm({ ...form, institute: e.target.value })}
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all">
                                <option value="engineering">Engineering</option>
                                <option value="management">Management</option>
                                <option value="both">Both</option>
                            </select>
                        </div>
                        <div className="flex flex-col gap-1.5">
                            <label className="text-sm font-medium text-slate-300">Status</label>
                            <select value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}
                                className="px-4 py-2.5 bg-slate-800 border border-slate-700 rounded-xl text-white text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all">
                                <option value="draft">Draft</option>
                                <option value="published">Published</option>
                                <option value="scheduled">Scheduled</option>
                                <option value="archived">Archived</option>
                            </select>
                        </div>
                    </div>
                </div>

                <div className="flex items-center justify-end gap-3 p-6 border-t border-slate-800">
                    <button onClick={onClose} className="px-5 py-2.5 bg-slate-800 hover:bg-slate-700 text-slate-300 text-sm rounded-xl transition-colors">
                        Cancel
                    </button>
                    <button onClick={() => onSave(form)} className="px-5 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white text-sm font-semibold rounded-xl transition-colors flex items-center gap-2">
                        <Check className="w-4 h-4" />
                        {research ? "Save Changes" : "Create Research"}
                    </button>
                </div>
            </motion.div>
        </div>
    );
}

function DeleteConfirmModal({ onClose, onConfirm, title }: {
    onClose: () => void;
    onConfirm: () => void;
    title: string;
}) {
    return (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4">
            <motion.div
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                className="bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-[420px] p-6"
            >
                <div className="flex items-center gap-3 mb-4">
                    <div className="w-10 h-10 rounded-full bg-red-500/10 flex items-center justify-center">
                        <AlertTriangle className="w-5 h-5 text-red-400" />
                    </div>
                    <div>
                        <h3 className="text-white font-semibold">Delete Research</h3>
                        <p className="text-slate-400 text-sm">This action cannot be undone.</p>
                    </div>
                </div>
                <p className="text-slate-300 text-sm mb-6">
                    Are you sure you want to delete "<span className="text-white font-medium">{title}</span>"?
                </p>
                <div className="flex items-center justify-end gap-3">
                    <button onClick={onClose} className="px-5 py-2.5 bg-slate-800 hover:bg-slate-700 text-slate-300 text-sm rounded-xl transition-colors">
                        Cancel
                    </button>
                    <button onClick={onConfirm} className="px-5 py-2.5 bg-red-600 hover:bg-red-500 text-white text-sm font-semibold rounded-xl transition-colors">
                        Delete
                    </button>
                </div>
            </motion.div>
        </div>
    );
}

export default function ResearchManager() {
    const { token } = useAdminAuth();
    const [researchList, setResearchList] = useState<ResearchItem[]>([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState("");
    const [catFilter, setCatFilter] = useState("All");
    const [modalOpen, setModalOpen] = useState(false);
    const [editResearch, setEditResearch] = useState<ResearchItem | null>(null);
    const [deleteTarget, setDeleteTarget] = useState<ResearchItem | null>(null);

    const headers = {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
    };

    const fetchResearch = async () => {
        try {
            const res = await fetch("/api/admin/research", { headers });
            if (res.ok) {
                const data = await res.json();
                setResearchList(data);
            }
        } catch {
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => { fetchResearch(); }, []);

    const filtered = researchList.filter(r => {
        const matchCat = catFilter === "All" || r.category === catFilter;
        const matchSearch = !search || (r.titleEn || "").toLowerCase().includes(search.toLowerCase()) || (r.titleAr || "").includes(search);
        return matchCat && matchSearch;
    });

    const handleSave = async (form: Omit<ResearchItem, "id"> & { id?: string }) => {
        try {
            if (editResearch) {
                const res = await fetch(`/api/admin/research/${editResearch.id}`, {
                    method: "PUT", headers, body: JSON.stringify(form),
                });
                if (res.ok) {
                    const updated = await res.json();
                    setResearchList(prev => prev.map(r => r.id === editResearch.id ? updated : r));
                }
            } else {
                const res = await fetch("/api/admin/research", {
                    method: "POST", headers, body: JSON.stringify(form),
                });
                if (res.ok) {
                    const created = await res.json();
                    setResearchList(prev => [...prev, created]);
                }
            }
        } catch {
        }
        setModalOpen(false);
        setEditResearch(null);
    };

    const handleDelete = async () => {
        if (!deleteTarget) return;
        try {
            const res = await fetch(`/api/admin/research/${deleteTarget.id}`, {
                method: "DELETE", headers,
            });
            if (res.ok) {
                setResearchList(prev => prev.filter(r => r.id !== deleteTarget.id));
            }
        } catch {
        }
        setDeleteTarget(null);
    };

    return (
        <AdminLayout>
            {(modalOpen || editResearch) && (
                <ResearchModal
                    research={editResearch}
                    onClose={() => { setModalOpen(false); setEditResearch(null); }}
                    onSave={handleSave}
                />
            )}

            {deleteTarget && (
                <DeleteConfirmModal
                    title={deleteTarget.titleEn || deleteTarget.titleAr}
                    onClose={() => setDeleteTarget(null)}
                    onConfirm={handleDelete}
                />
            )}

            <div className="p-6 md:p-8 max-w-[1400px] mx-auto">
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-8">
                    <div>
                        <h1 className="text-2xl font-bold text-white">Research Manager</h1>
                        <p className="text-slate-400 text-sm mt-1">{researchList.length} total publications</p>
                    </div>
                    <button
                        onClick={() => { setEditResearch(null); setModalOpen(true); }}
                        className="flex items-center gap-2 px-5 py-2.5 bg-indigo-600 hover:bg-indigo-500 text-white text-sm font-semibold rounded-xl transition-colors shadow-lg shadow-indigo-500/20"
                    >
                        <Plus className="w-4 h-4" />
                        New Research
                    </button>
                </div>

                <div className="grid grid-cols-4 gap-4 mb-6">
                    {[
                        { label: "Published", count: researchList.filter(r => r.status === "published").length, color: "text-emerald-400" },
                        { label: "Drafts", count: researchList.filter(r => r.status === "draft").length, color: "text-slate-400" },
                        { label: "Scheduled", count: researchList.filter(r => r.status === "scheduled").length, color: "text-amber-400" },
                        { label: "Archived", count: researchList.filter(r => r.status === "archived").length, color: "text-red-400" },
                    ].map((s, i) => (
                        <div key={i} className="bg-slate-900 border border-slate-800 rounded-xl p-4 text-center">
                            <p className={`text-2xl font-bold ${s.color}`}>{s.count}</p>
                            <p className="text-xs text-slate-500 mt-0.5">{s.label}</p>
                        </div>
                    ))}
                </div>

                <div className="flex flex-col md:flex-row gap-4 mb-6">
                    <div className="relative flex-1 max-w-sm">
                        <Search className="absolute left-3.5 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                        <input value={search} onChange={e => setSearch(e.target.value)} placeholder="Search research..."
                            className="w-full pl-10 pr-4 py-2.5 bg-slate-900 border border-slate-800 rounded-xl text-white text-sm placeholder-slate-500 focus:outline-none focus:ring-2 focus:ring-indigo-500/50 focus:border-indigo-500 transition-all" />
                    </div>
                    <div className="flex flex-wrap gap-2">
                        {categories.map(c => (
                            <button key={c} onClick={() => setCatFilter(c)}
                                className={`px-4 py-2 rounded-xl text-sm transition-all ${catFilter === c ? "bg-indigo-600 text-white" : "bg-slate-900 border border-slate-800 text-slate-400 hover:text-white hover:border-slate-600"}`}>
                                {c}
                            </button>
                        ))}
                    </div>
                </div>

                {loading ? (
                    <div className="flex flex-col items-center justify-center py-20 text-slate-500">
                        <Clock className="w-12 h-12 mb-3 opacity-30 animate-spin" />
                        <p className="text-sm">Loading research...</p>
                    </div>
                ) : (
                    <>
                        <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-5">
                            {filtered.map((item, i) => (
                                <motion.div
                                    key={item.id}
                                    initial={{ opacity: 0, y: 16 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: i * 0.06 }}
                                    className="bg-slate-900 border border-slate-800 rounded-2xl overflow-hidden group"
                                >
                                    <div className="relative h-[120px] overflow-hidden bg-slate-800 flex items-center justify-center">
                                        <FlaskConical className="w-10 h-10 text-slate-700" />
                                        <div className="absolute top-3 left-3 flex gap-2">
                                            <span className={`text-[11px] px-2.5 py-0.5 rounded-full font-medium ${statusColor[item.status] || statusColor.draft}`}>
                                                {item.status}
                                            </span>
                                        </div>
                                        <div className="absolute top-3 right-3 flex gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                                            <button onClick={() => setEditResearch(item)}
                                                className="w-8 h-8 bg-slate-900/80 backdrop-blur-sm rounded-lg flex items-center justify-center text-white hover:bg-indigo-600 transition-colors">
                                                <Edit2 className="w-3.5 h-3.5" />
                                            </button>
                                            <button onClick={() => setDeleteTarget(item)}
                                                className="w-8 h-8 bg-slate-900/80 backdrop-blur-sm rounded-lg flex items-center justify-center text-white hover:bg-red-600 transition-colors">
                                                <Trash2 className="w-3.5 h-3.5" />
                                            </button>
                                        </div>
                                    </div>

                                    <div className="p-4">
                                        <h3 className="text-white font-semibold text-sm line-clamp-2 mb-1">{item.titleEn || item.titleAr}</h3>
                                        {item.journal && (
                                            <p className="text-slate-500 text-xs flex items-center gap-1.5 mb-1">
                                                <BookOpen className="w-3 h-3" />
                                                {item.journal}
                                            </p>
                                        )}
                                        {item.publishedYear && (
                                            <p className="text-slate-500 text-xs flex items-center gap-1.5 mb-1">
                                                <GraduationCap className="w-3 h-3" />
                                                {item.publishedYear}
                                            </p>
                                        )}
                                        <div className="flex items-center gap-2 mt-3 flex-wrap">
                                            {item.category && (
                                                <span className="text-[10px] px-2 py-0.5 bg-slate-800 text-slate-400 rounded-full">
                                                    {item.category}
                                                </span>
                                            )}
                                            <span className="text-[10px] px-2 py-0.5 bg-slate-800 text-slate-400 rounded-full flex items-center gap-1">
                                                <Building2 className="w-2.5 h-2.5" />
                                                {item.institute}
                                            </span>
                                            {item.doi && (
                                                <span className="text-[10px] px-2 py-0.5 bg-slate-800 text-slate-400 rounded-full flex items-center gap-1">
                                                    <LinkIcon className="w-2.5 h-2.5" />
                                                    DOI
                                                </span>
                                            )}
                                            {item.fileUrl && (
                                                <span className="text-[10px] px-2 py-0.5 bg-slate-800 text-slate-400 rounded-full flex items-center gap-1">
                                                    <FileText className="w-2.5 h-2.5" />
                                                    PDF
                                                </span>
                                            )}
                                            {item.externalUrl && (
                                                <span className="text-[10px] px-2 py-0.5 bg-slate-800 text-slate-400 rounded-full flex items-center gap-1">
                                                    <ExternalLink className="w-2.5 h-2.5" />
                                                    Link
                                                </span>
                                            )}
                                        </div>
                                    </div>
                                </motion.div>
                            ))}
                        </div>

                        {filtered.length === 0 && (
                            <div className="flex flex-col items-center justify-center py-20 text-slate-500">
                                <FlaskConical className="w-12 h-12 mb-3 opacity-20" />
                                <p className="text-sm">No research found</p>
                                <button onClick={() => { setEditResearch(null); setModalOpen(true); }}
                                    className="mt-3 text-indigo-400 text-sm hover:text-indigo-300 transition-colors">
                                    + Add your first research
                                </button>
                            </div>
                        )}
                    </>
                )}
            </div>
        </AdminLayout>
    );
}

